mapper.select:
select ct.*, sf.subofficename, date_format(ct.signtime, '%Y-%m-%d') signtimestr, case when ct.status = '9' then '已审核' when ct.status = '2' then '审核中' else '未审核' end statusstr from t_c_contract ct , t_s_suboffice sf where ct.subofficeid = sf.subofficeid and ct.isdisabled=0
and ct.subofficeid = #{subofficeid}
and ct.contractname like CONCAT('%',#{contractname},'%')

mapper.update:
update t_c_contract set isdisabled = 1 where 1=1
and contractid in
#{idList}

mapper.select:
select b.*,a.monthamount,a.year,a.month,a.contractexecuteid from t_c_contractexecute a,( select ct.contractid,ct.contractname,ct.contractnum,s.subofficeid,s.subofficename, ct.amount,sum(cte.monthamount) amountyear, convert(sum(cte.monthamount)/ct.amount*100,decimal(15,2))percent from t_c_contractexecute cte,t_c_contract ct,t_s_suboffice s where cte.contractid=ct.contractid and ct.subofficeid=s.subofficeid and cte.isdisabled=0 group by cte.contractid) b where a.contractid = b.contractid and a.isdisabled=0
and a.year = #{year}
and a.month = #{month}
and b.subofficeid = #{subofficeid}
and b.contractname like CONCAT('%',#{contractname},'%')

mapper.update:
update t_c_contractexecute set isdisabled = 1 where 1=1
and contractexecuteid in
#{idList}
