mapper.select:
select tdid,showtitle,mrowspan,mcolspan,rowindex
	from t_s_dcdytitles t
where t.dcdytype = #{dcdytype}

	isdytype = #{isdytype}


	isdctype = #{isdctype}

order by t.rowindex,colindex

select t.keyname,t.talign,t.formartstr from t_s_dcdysqlproperty t 
	where t.dcdytype = #{dcdytype} 
	
		protype = #{protype}
	
	order by orderby
